CommandUIHandler.CommandAction:
javascript:
            function onSucceedCopyQuery(result, userContext, methodName)
            {
                if (result)
                {
                    alert(result);
                }
                SP.UI.ModalDialog.RefreshPage(1);
            }
            function onFailCopyQuery(error, userContext, methodName)
            {
            }
            var selectedItems = SP.ListOperation.Selection.getSelectedItems();
            var selectedItemId = selectedItems[0].id;
            var parameters = { selectedId: selectedItemId };
            Sys.Net.WebServiceProxy.invoke("{SiteUrl}/_vti_bin/DiscoveryInternalService.asmx", 'CopySavedSearch', false, parameters, onSucceedCopyQuery, onFailCopyQuery, null, Sys.Net.WebRequestManager.get_defaultTimeout(), false, null);

CommandUIHandler.EnabledScript:
javascript:
           function enabled()
           {
             var items = SP.ListOperation.Selection.getSelectedItems();
             return (items.length == 1);
           }
           enabled();

CommandUIHandler.CommandAction:
javascript:
            function onSucceedUpdateStatistics(result, userContext, methodName)
            {
                if (result)
                {
                    alert(result);
                }
                SP.UI.ModalDialog.RefreshPage(1);
            }
            function onFailUpdateStatistics(error, userContext, methodName)
            {
            }
            var selectedItems = SP.ListOperation.Selection.getSelectedItems();
            var selectedItemIds = '';
            var selectedItemIndex;
            for (selectedItemIndex in selectedItems){
                if (selectedItemIds)
                    selectedItemIds +='|';
                selectedItemIds += selectedItems[selectedItemIndex].id;
            }
            var parameters = { selectedIds: selectedItemIds };
            Sys.Net.WebServiceProxy.invoke("{SiteUrl}/_vti_bin/DiscoveryInternalService.asmx", 'UpdateStatistics', false, parameters, onSucceedUpdateStatistics, onFailUpdateStatistics, null, Sys.Net.WebRequestManager.get_defaultTimeout(), false, null);

CommandUIHandler.EnabledScript:
javascript:
         function enabled()
         {
             var items = SP.ListOperation.Selection.getSelectedItems();
             return (items.length > 0);
         }
         enabled();

CommandUIHandler.CommandAction:
javascript:
         var selectedItems = SP.ListOperation.Selection.getSelectedItems();
         var selectedItemIds = '';
         var selectedItemIndex;
         for (selectedItemIndex in selectedItems){
          if (selectedItemIds)
            selectedItemIds +='|';
          selectedItemIds += selectedItems[selectedItemIndex].id;
          }
         window.location.href = "{SiteUrl}" + '/_layouts/15/ExportPage.aspx' + '?selectedItems='+selectedItemIds;

